mapper.select:
select * from( select rownum rnum ,a.* from (select post_no, post_mem, post_ttl, post_cntnt, post_date, post_star, post_gu, post_dong, post_zip, post_rd, mem_email from post inner join member on(post_mem = mem_no) inner join article on(post_gu = artcl_gu and post_dong = artcl_dong and post_zip = artcl_zip and post_rd = artcl_rd) where post_brd = 1
and (mem_email like '%' || #{searchNm} || '%'
and (post_date = #{searchNm}
and (mem_email like '%' || #{searchNm} || '%' or post_cntnt like '%' || #{searchNm} || '%' or post_ttl like '%' || #{searchNm} || '%'
)
and post_exst = 'N' ) a) where rnum between #{page}*#{pageSize}-(#{pageSize}-1) and #{page}*#{pageSize}

select count(*) from post inner join member on(post_mem = mem_no) inner join article on(post_gu = artcl_gu and post_dong = artcl_dong and post_zip = artcl_zip and post_rd = artcl_rd) where post_brd = 1
and (mem_email like '%' || #{searchNm} || '%'
and (post_date = #{searchNm}
and (mem_email like '%' || #{searchNm} || '%' or post_cntnt like '%' || #{searchNm} || '%' or post_ttl like '%' || #{searchNm} || '%'
)
and post_exst = 'N'

mapper.update:
update post
set post_exst = 'Y'
where post_no = #{post_no}

mapper.select:
select picture.* 
from picture inner join post on(pic_post = post_no)
where post_no = #{post_no}
